import randomdef jogar():    print("----------------------------------")    print("-Bem-vindo ao jogo de adivinhação-")    print("----------------------------------")    numero_secreto = random.randrange(1,101)    tentativa_total = 0    pontos = 1000    print("\nDefina o nível de dificuldade do seu jogo")    print("(1) Fácil (2) Médio (3) Difícil")    dificuldade = int(input("Insira o número da dificuldade: "))    if dificuldade == 1:        print("Você escolheu a dificuldade FÁCIL. Boa sorte!\n")        tentativa_total = 15    elif dificuldade == 2:        print("Você escolheu a dificuldade MÉDIA. Boa sorte!\n")        tentativa_total = 10    else:        print("Você escolheu a dificuldade DIFÍCIL. Boa sorte!\n")        tentativa_total = 5    for numero_palpite in range(1, tentativa_total + 1):        print("Tentativa {} de {}".format(numero_palpite, tentativa_total))        palpite = input('Digite seu palpite > ')        # Faz conversão da váriavel palpite de string para int e a armazena em 'chute'        chute = int(palpite)        acertou = chute == numero_secreto        maior = chute > numero_secreto        menor = chute < numero_secreto        if chute < 1 or chute > 100:            print("Atenção! O valor do palpite deve ser entre 1 e 100!")            continue        if acertou:            print("Acertou! O número secreto era {} e você fez {} pontos!".format(numero_secreto, pontos))            break        else:            if maior:                print("Você errou! O valor do número secreto é menor!")            elif menor:                print("Você errou! O valor do numero secreto é maior!")            pontos_perdidos = abs(numero_secreto - chute)            pontos = pontos - pontos_perdidos    print("Fim de jogo!")if __name__ == "__main__":    jogar()