import forcaimport adivinhacaodef escolher_jogo():    print("==================================")    print("===Bem-vindo a Seleção de Jogos===")    print("==================================")    jogos = int(input("Use (1) para Adivinhação e (2) para Forca! -> "))    if jogos == 1:        print("\n - Você selecionou Adivinhação! - \n")        adivinhacao.jogar()    elif jogos == 2:        print("\n - Você selecionou Forca! - \n")        forca.jogar()if __name__ == "__main__":    escolher_jogo()